#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\ThanhPhuong\Hoc Tap\HK2-2024-2025\Phat trien ung dung web\PTUDW-22850211\node_modules\.pnpm\mime@1.6.0\node_modules\mime\node_modules;D:\ThanhPhuong\Hoc Tap\HK2-2024-2025\Phat trien ung dung web\PTUDW-22850211\node_modules\.pnpm\mime@1.6.0\node_modules;D:\ThanhPhuong\Hoc Tap\HK2-2024-2025\Phat trien ung dung web\PTUDW-22850211\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/ThanhPhuong/Hoc Tap/HK2-2024-2025/Phat trien ung dung web/PTUDW-22850211/node_modules/.pnpm/mime@1.6.0/node_modules/mime/node_modules:/mnt/d/ThanhPhuong/Hoc Tap/HK2-2024-2025/Phat trien ung dung web/PTUDW-22850211/node_modules/.pnpm/mime@1.6.0/node_modules:/mnt/d/ThanhPhuong/Hoc Tap/HK2-2024-2025/Phat trien ung dung web/PTUDW-22850211/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../mime/cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../mime/cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../mime/cli.js" $args
  } else {
    & "node$exe"  "$basedir/../mime/cli.js" $args
  }
  $ret=$LASTEXITCODE
}
